import 'package:flutter/material.dart';import 'package:shopping_list/data/dummy_items.dart';final theme = ThemeData.dark().copyWith(  useMaterial3: true,  colorScheme: ColorScheme.fromSeed(    seedColor: const Color.fromARGB(255, 147, 229, 250),    brightness: Brightness.dark,    surface: const Color.fromARGB(255, 42, 51, 59)  ),  scaffoldBackgroundColor: const Color.fromARGB(255, 50, 58, 60));void main() {  runApp(const MyApp());}class MyApp extends StatelessWidget {  const MyApp({super.key});  // This widget is the root of your application.  @override  Widget build(BuildContext context) {    return MaterialApp(      title: 'Flutter Demo',      theme: theme,      home: MyHomePage(),    );  }}class MyHomePage extends StatelessWidget {  final itemList = groceryItems;  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(title: Text("Your Groceries"),),      body: ListView.builder(        itemCount: itemList.length,        itemBuilder: (context, index) => ListTile(          leading: Icon(Icons.rectangle, color: itemList[index].category.color),          title: Text(itemList[index].name),          trailing: Text("${itemList[index].quantity}"),        ),      ),    );  }}